mapper.select:
select count(*)
  from app_form
 where app_form_name = #{appFormName}

mapper.insert:
insert
	into app_form
			 (
			 	 app_no
			 , app_form_name
			 , app_form
			 , app_category
			 , tmp
			 )
values (
				 seq_app_no.nextval
			 , #{appFormName}
			 , #{appForm}
			 , #{appCategory}
			 , #{tmp}
			 )
