mapper.insert:
insert
	into app_form
			 (
			 	 app_no
			 , app_form_name
			 , app_form
			 , app_category
			 , tmp
			 )
values (
				 seq_app_no.nextval
			 , #{appFormName}
			 , #{appForm}
			 , #{appCategory}
			 , #{tmp}
			 )

mapper.select:
select app_form
  from app_form
 where app_no = #{appNo}

mapper.insert:
insert
  into app_doc
       (
       doc_content
     , drafter
     , pre_period
     , app_no
     , doc_no
     , doc_title
     , status
       )
values (
       #{docContent}
     , #{drafter}
     , #{prePeriod}
     , #{appNo}
     , seq_doc_no.nextval
     , #{docTitle}
     , #{status}
       )

insert
  into app_attach
       (
       file_no
     , doc_no
     , file_path
     , original_name
     , filesystem_name
       )
values (
       seq_app_file_no.nextval
     , seq_doc_no.currval
     , #{filePath}
     , #{originalName}
     , #{filesystemName}
       )

mapper.select:
select count(*)
  from app_doc
  join app_form on (app_doc.app_no = app_form.app_no)
 where drafter = #{loginUserNo}
   and to_char(draft_date, 'YYYY-MM-DD') between #{startDate} and #{endDate}


    and status not in (0, 40)


		and status = #{docStatus}



	 and app_category = #{category}


   and app_doc.status = #{status}


	 and doc_title like '%' || #{search} || '%'


	 and doc_no like '%' || #{search} || '%'

select doc_no , doc_title , to_char(draft_date, 'YYYY-MM-DD') draft_date , app_category , status_name from app_doc join app_status on (status = status_no) join app_form on (app_doc.app_no = app_form.app_no) where drafter = #{loginUserNo} and to_char(draft_date, 'YYYY-MM-DD') between #{startDate} and #{endDate}
and status not in (0, 40)
and status = #{docStatus}
and app_category = #{category}
and app_doc.status = #{status}
and doc_title like '%' || #{search} || '%'
and doc_no like '%' || #{search} || '%'
order by draft_date

		select count(*)
		  from app_doc
		  join app_form on (app_doc.app_no = app_form.app_no)
		  join member on ( app_doc.drafter = member.mem_no )
     where (
			       select a.approver
			         from (
			               select approver
			                   , doc_no
			                   , ROW_NUMBER() OVER (PARTITION BY approver.doc_no ORDER BY app_type, approver) AS rnum
			                 from approver
			                where app_yn = 'N'
			              ) a
			        where rnum = 1
			          and doc_no = app_doc.doc_no
			   	 ) = #{loginUserNo}
		   and to_char(draft_date, 'YYYY-MM-DD') between #{startDate} and #{endDate}
		   and app_doc.status not in (0, 40)

			 and app_category = #{category}


		   and app_doc.status = #{status}


			 and doc_title like '%' || #{search} || '%'


			 and mem_name like '%' || #{search} || '%'

select doc_no , doc_title , to_char(draft_date, 'YYYY-MM-DD') draft_date , app_category , status_name , mem_name from app_doc join app_status on (status = status_no) join app_form on (app_doc.app_no = app_form.app_no) join member on (drafter = mem_no) where ( select a.approver from ( select approver , doc_no , ROW_NUMBER() OVER (PARTITION BY approver.doc_no ORDER BY app_type, approver) AS rnum from approver where app_yn = 'N' ) a where rnum = 1 and doc_no = app_doc.doc_no ) = #{loginUserNo} and to_char(draft_date, 'YYYY-MM-DD') between #{startDate} and #{endDate} and app_doc.status not in (0, 40)
and app_category = #{category}
and app_doc.status = #{status}
and doc_title like '%' || #{search} || '%'
and mem_name like '%' || #{search} || '%'
