mapper.update:
update stu_attend
	 set attendance = #{attendance}
 where stu_no =#{stuNo}
	 and lec_no =#{lecNo}
	 and to_char(lecture_date,'YY/MM/DD')=to_char(sysdate, 'YY/MM/DD')

mapper.insert:
		insert
			into open_lec
		(
				OPEN_LEC_NO
      , LEC_NO
      , START_DATE
      , END_DATE
      , LECTURE_DATE
      , START_TIME
      , END_TIME
      , RES_NO
      , LEC_STATUS
		)
	 values
		(
				seq_open_lec_no.nextval
    	,	#{lecNo}
			, #{startDate}
			, #{endDate}
			, #{lectureDate}
			, #{startTime}
    	, #{endTime}
			, #{resNo}
			, 'Y'
		)

mapper.update:
		update open_lec
			 set open_lec_no = #{openLecNo}
         , LEC_NO =#{lecNo}
         , START_DATE=#{startDate}
         , END_DATE=#{endDate}
         , LECTURE_DATE=#{lectureDate}
         , START_TIME=#{startTime}
         , END_TIME=#{endTime}
         , RES_NO=#{resNo}
		 where open_lec_no = #{openLecNo}


update open_lec
	 set LEC_STATUS = 'N'
 where open_lec_no = #{openLecNo}
